require 'bundler'
Bundler.setup 
require 'gmail'

$user = "username@gmail.com"
$pass = "userpassword"


def shoot(email, attachment)
  gmail = Gmail.connect($user, $pass)
  gmail.deliver do
    to email
    subject "Sending myself a file"
    text_part do
      body "Sending you #{attachment}"
    end
    add_file "#{attachment}"
  end
  gmail.logout
  puts "sent #{attachment}"
end

